from aiogram.filters import CommandStartfrom aiogram.types import Message, FSInputFile, PollAnswer, InputMediaPhotofrom aiogram.fsm.context import FSMContextfrom aiogram import Routerfrom markup import anket_menufrom states import Formfrom random import randintrouter = Router()@router.message(CommandStart())async def start(message: Message, state: FSMContext):    await state.set_state(Form.wait)    text = ('заполни анкету нажми на кнопку ниже чтобы начать \n прожмите /help если хотите узнать о боте больше')    await message.answer(text=text, reply_markup=anket_menu)    await message.delete()@router.poll_answer()async def poll_answer(poll_answer: PollAnswer):    answser_ids = poll_answer.option_ids    print(answser_ids)